template.value:
import React, {Component} from 'react';
import config from '@/commons/config-hoc';
import PageContent from '@/layouts/page-content';
import {
    QueryBar,
    QueryItem,
    Pagination,
    ToolBar,
    Operator
} from "@/library/antd";
import Table from '@/components/table';
import EditModal from './EditModal';

@config({
    path: '/xxx',
    ajax: true,
})
export default class index extends Component {
    state = {
        loading: false,
        dataSource: [],     // 表格数据
        total: 0,           // 分页中条数
        pageSize: 50,       // 分页每页显示条数
        pageNum: 1,         // 分页当前页
        params: {},         // 查询条件
        visible: false,
        id: null,
    };

    queryItems = [
        [
            {
                field: 'name',
                label: '名称',
                width: 300,
            },
        ],
    ];

    columns = [
        {title: '编号', key: '__num', width: 40, render: (value, record, index) => (index + 1) + this.state.pageSize * (this.state.pageNum - 1)},
        {title: '名称', dataIndex: 'name', width: 100},
        {
            title: '操作',
            dataIndex: 'operator',
            width: 200,
            render: (value, record) => {
                const {id} = record;
                const items = [
                    {
                        label: '编辑',
                        onClick: () => this.setState({visible: true, id}),
                    },
                    {
                        label: '删除',
                        color: 'red',
                        confirm: {
                            title: '您确定删除此条记录？',
                            onConfirm: () => this.handleDelete(id),
                        }
                    },
                ];
                return <Operator items={items}/>
            },
        }
    ];

    componentDidMount() {
        this.handleSearch();
    }

    handleSearch = () => {
        if (this.state.loading) return;

        const {params, pageNum, pageSize} = this.state;

        this.setState({loading: true});
        // TODO
        this.props.ajax
            .get('/xxx', {...params, pageNum, pageSize})
            .then((res) => {
                const dataSource = res?.recordList || [];
                const total = res?.recordCount || 0;

                this.setState({dataSource, total});
            })
            .finally(() => this.setState({loading: false}));
    };

    handleDelete = (id) => {
        this.props.ajax.del(`/xxx/${id}`, null, {successTip: '删除成功！'})
            .then(() => {
                this.handleSearch();
            });
    };

    render() {
        const {
            total,
            pageNum,
            pageSize,
            dataSource,
            loading,
            visible,
            id,
        } = this.state;
        return (
            <PageContent>
                <QueryBar>
                    <QueryItem
                        items={this.queryItems}
                        onSubmit={params => this.setState({params}, this.handleSearch)}
                    />
                </QueryBar>

                <ToolBar
                    items={[
                        {text: '新建', icon: 'plus', onClick: () => this.setState({visible: true, id: null})},
                    ]}
                />

                <Table
                    loading={loading}
                    columns={this.columns}
                    dataSource={dataSource}
                    rowKey="id"
                    pagination={false}
                />

                <Pagination
                    total={total}
                    pageNum={pageNum}
                    pageSize={pageSize}
                    onPageNumChange={pageNum => this.setState({pageNum}, this.handleSearch)}
                    onPageSizeChange={pageSize => this.setState({pageSize, pageNum: 1}, this.handleSearch)}
                />

                <EditModal
                    visible={visible}
                    id={id}
                    onOk={() => this.setState({visible: false}, this.handleSearch)}
                    onCancel={() => this.setState({visible: false})}
                />
            </PageContent>
        );
    }
}

import React, {Component} from 'react';
import {Form, Row, Col, Button, Spin} from 'antd';
import _ from 'lodash';
import {FormElement} from '@/library/antd';
import PageContent from '@/layouts/page-content';
import config from '@/commons/config-hoc';
import validator from '@/library/utils/validation-rule';
import modal from '@/components/modal-hoc';

@config({ajax: true})
@Form.create()
@modal(props => props.id === null ? '添加用户' : '修改用户')
export default class EditModal extends Component {
    state = {
        loading: false,
        data: {}, // 表单回显数据
    };

    componentDidMount() {
        const {id} = this.props;

        const isEdit = id !== null;

        if (isEdit) {
            this.setState({loading: true});
            this.props.ajax.get(`/xxx/${id}`)
                .then(res => {
                    this.setState({data: res || {}});
                })
                .finally(() => this.setState({loading: false}));
        }
    }


    handleOk = () => {
        if (this.state.loading) return; // 防止重复提交

        this.props.form.validateFieldsAndScroll((err, values) => {
            if (err) return;

            const {id} = this.props;
            const isEdit = id !== null;

            if (isEdit) {
                this.setState({loading: true});
                this.props.ajax.put('/xxx', values, {successTip: '修改成功！'})
                    .then(() => {
                        const {onOk} = this.props;
                        onOk && onOk();
                    })
                    .finally(() => this.setState({loading: false}));
            } else {
                this.props.ajax.post('/xxx', values, {successTip: '添加成功！'})
                    .then(() => {
                        const {onOk} = this.props;
                        onOk && onOk();
                    })
                    .finally(() => this.setState({loading: false}));
            }
        });
    };


    // 节流校验写法
    userNameExist = _.debounce((rule, value, callback) => {
        console.log('节流发请求');
    }, 500);

    handleCancel = () => {
        const {onCancel} = this.props;
        onCancel && onCancel();
    };

    handleReset = () => {
        this.props.form.resetFields();
    };

    // 这样可以保证每次render时，FormElement不是每次都创建，这里可以进行一些共用属性的设置
    FormElement = (props) => <FormElement form={this.props.form} labelWidth={100} disabled={this.props.isDetail} {...props}/>;

    render() {
        const {id} = this.props;
        const isEdit = id !== null;
        const {loading, data} = this.state;

        const FormElement = this.FormElement;
        return (
            <Spin spinning={loading}>
                <PageContent footer={false}>
                    <Form onSubmit={this.handleSubmit}>
                        {isEdit ? <FormElement type="hidden" field="id" initialValue={data.id}/> : null}
                        <Row>
                            <Col span={24}>
                                <FormElement
                                    label="名称"
                                    field="name"
                                    initialValue={data.name}
                                    required
                                    rules={[
                                        validator.noSpace(),
                                        validator.userNameExist(),
                                        {validator: this.userNameExist}
                                    ]}
                                />
                            </Col>
                        </Row>
                    </Form>
                </PageContent>
                <div className="ant-modal-footer">
                    <Button onClick={this.handleOk} type="primary">保存</Button>
                    <Button onClick={this.handleReset}>重置</Button>
                    <Button onClick={this.handleCancel}>取消</Button>
                </div>
            </Spin>
        );
    }
}
